import React from "react";
import {makeStyles} from '@material-ui/core/styles'
import { SideBarData } from "./SideBarData";

const StyledSideBar = makeStyles({
    side:{
        height: "calc(100vh - 80px)",
        width: "20%",
        paddingTop: "0.5rem",
        backgroundColor: "#fff",
        ['@media (max-width:780px)']: {
            flexDirection: "column"
        }
    },
    sidebarList: {
        height: "100%",
        padding: "0",
        width: "90%",
        paddingTop: "3rem",
        paddingLeft: "0.5rem",
    },
    row: {
        marginBottom: "4rem",
        marginTop: "0.5rem",
        // marginRight: "2rem",
        width: "90%",
        height: "8%",
        listStyleType: "none",
        fontFamily: "Muli",
        fontSize: "36",
        display: "flex",
        flexDirection: "row",
        justifyContent: "left",
        alignItems: "center",
        border: "1px solid #e30217",
        paddingLeft: "1rem"
    }
})
function CustomSideBar() {
    const classess = StyledSideBar()
    return (
        <div className={classess.side}>
            <ul className={classess.sidebarList}>
            {SideBarData.map((val, key)=> {
                return <li key={key} className={classess.row} onClick={()=> {window.location.pathname = val.link}}> 
                {" "}
                <div>{val.title}</div></li>
            })}
            </ul>
        </div>
    )
}

export default CustomSideBar
