function CustomSideBar() {
    // const classess = StyledSideBar()
    return (
        console.log(window.location.pathname),
        <div className="side">
            <ul className="sidebarList">
                {SideBarData.map((val, key) => {
                    return <li
                        key={key}
                        className="row"
                        id={window.location.pathname == val.path ? "active" : ""}
                    // onClick={()=> {
                    //     window.location.pathname = val.link;

                    //     }}
                    >
                        <Link to={val.path}></Link>
                        <Router>
                            <Routes>
                                <Route path='/' exact component={DashBoard}></Route>
                                <Route path='src\components\SideBarPages\accounts.js' component={Accounts}></Route>
                                <Route path='/meterials' component={Materials}></Route>
                                <Route path='/products' component={Products}></Route>
                                <Route path='/components' component={Components}></Route>

                            </Routes>
                        </Router>
                        <div>{val.title}</div></li>
                })}
            </ul>
        </div>
    )
}

export default CustomSideBar
