import React from 'react'
import './MaterialPopup.css'
import CloseIcon from '@mui/icons-material/Close'
import { Button, InputAdornment, makeStyles, TextField } from '@mui/material';
import { alpha, styled } from '@mui/material/styles';


const status = [
    {
        value:'available',
        label: 'Available'
    },
    {
        value:'unavailable',
        label: 'Unavailable'
    }
]

const CssTextField = styled(TextField)({
    'width': '100%',
    '& label.Mui-focused': {
        color: 'black',
    },
    '& .MuiInput-underline:after': {
        borderBottomColor: '#e30217',
    },
    '& .MuiOutlinedInput-root': {
        '& fieldset': {
            borderColor: 'black',
        },
        '&:hover fieldset': {
            borderColor: '#e30217',
        },
        '&.Mui-focused fieldset': {
            borderColor: '#e30217',
        },
    },
});

function MaterialPopup(props) {
    return (props.trigger) ? (
        <div className='componentpopup'>
            <div className='popup-inner'>
                <div><button className='close-btn' onClick={() => props.setTrigger(false)}>
                    <CloseIcon style={{ 'color': "white", 'widtth': "100%", 'height': "100%", }} />
                </button></div>
                {props.children}
                <div className='popup-body'>
                    <form>
                        <div className='imagefield'>
                            <CssTextField label="Image Path" id="fullWidth" required/>
                        </div>
                        <div className='idname'>
                            <div className='idfield'>
                                <CssTextField label="Material ID" id="fullWidth" required/>
                            </div>
                            <div className='namefield'>
                                <CssTextField label="Material Name" id="fullWidth" required/>
                            </div>
                        </div>
                        <div className='txtfield'>
                            <CssTextField label="Unit" id="fullWidth" required/>
                        </div>
                        <div className='txtfield'>
                            <CssTextField 
                            label="Amount" 
                            id="fullWidth" 
                            required type={'number'}
                            InputProps={{
                                inputProps: {min: 0, pattern: '[0-9]*' }
                            }}/>
                        </div>
                        <div className='txtfield'>
                            <CssTextField label="Status" id="fullWidth" required/>
                        </div>
                        <div className='btngr'>
                            <Button
                                variant="contained"
                                style={{ fontFamily: 'Muli', borderRadius: 10, backgroundColor: "#e30217", marginRight: '0.5rem' }}
                                size="large"
                            >Add Material</Button>
                            <Button
                                variant="contained"
                                style={{ fontFamily: 'Muli', borderRadius: 10, backgroundColor: "#e30217" }}
                                size="large"
                            >Cancel</Button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    ) : "";
}

export default MaterialPopup
