import React from 'react'
import './ComponentPopup.css'
import CloseIcon from '@mui/icons-material/Close'
import { InputAdornment, TextField } from '@mui/material';
import { alpha, styled } from '@mui/material/styles';

const CssTextField = styled(TextField)({
    '& label.Mui-focused': {
        color: 'green',
    },
    '& .MuiInput-underline:after': {
        borderBottomColor: 'green',
    },
    '& .MuiOutlinedInput-root': {
        '& fieldset': {
            borderColor: 'red',
        },
        '&:hover fieldset': {
            borderColor: 'yellow',
        },
        '&.Mui-focused fieldset': {
            borderColor: 'green',
        },
    },
});

function MaterialPopup(props) {
    return (props.trigger) ? (
        <div className='componentpopup'>
            <div className='popup-inner'>
                <div><button className='close-btn' onClick={() => props.setTrigger(false)}>
                    <CloseIcon style={{ 'color': "white", 'widtth': "100%", 'height': "100%", }} />
                </button></div>
                {props.children}
                <div className='popup-body'>
                    <form>
                        {/* <div className='txtfield'> */}
                            {/* <div className='divlabel'>
                                <label className='label'>
                                    Material ID</label>
                            </div> */}
                            <CssTextField label="Custom CSS" id="fullWidth" />
                        {/* </div> */}
                        <div className='txtfield'>
                            <div className='divlabel'>
                                <label className='label'>
                                    Material Name</label>
                            </div>
                            <input type="text" className='text' />
                        </div>
                        <div className='txtfield'>
                            <div className='divlabel'>
                                <label className='label'>
                                    Unit</label>
                            </div>
                            <input type="text" className='text' />
                        </div>
                        <div className='txtfield'>
                            <div className='divlabel'>
                                <label className='label'>
                                    Amount</label>
                            </div>
                            <input type="text" className='text' />
                        </div>
                        <div className='txtfield'>
                            <div className='divlabel'>
                                <label className='label'>
                                    Image</label>
                            </div>
                            <input type="text" className='text' />
                        </div>
                        <div className='txtfield'>
                            <div className='divlabel'>
                                <label className='label'>
                                    Status</label>
                            </div>
                            <input type="text" className='text' />
                        </div>
                    </form>
                </div>
            </div>
        </div>
    ) : "";
}

export default MaterialPopup
