import React, {useState} from 'react';
import MaterialTable from 'material-table';


export const Table=()=>{
    const [data, setData]=useState([
        {id:'123',workername:'Hieu',material:'Ống gas',amount:'100'},
        {id:'124',workername:'Taco',material:'Ống gas',amount:'200'},
        {id:'125',workername:'Bò',material:'Ống gas',amount:'300'},
        {id:'126',workername:'Trung',material:'Ống gas',amount:'400'},
    ])
    const columns=[
        {
            title:'ID', field:'id'
        },
        {
            title:'Worker Name', field:'workername'
        },
        {
            title:'Material', field:'material'
        },
        {
            title:'Amount', field:'amount'
        },
        // {
        //     title:'Detail', field:'workername'
        // },
        // {
        //     title:'Worker Name', field:'workername'
        // }
    ]
    return(
        <div>
            <MaterialTable title={"Material Requesting List"}
            data={data}
            columns={columns}
            editable={{
                onRowAdd: (newRow) => new Promise((resolve,reject) =>{
                    setData([...data, newRow])
                    resolve()
                }),
                onRowUpdate: (newRow) => Promise((resolve,reject) =>{
                    const updatedData=[...data]
                    updateData[oldRow.data.id] = newRow
                    setData(updatedData)
                    setTimeout(() => resolve(), 500)
                })
            }}
            localization={{
                toolbar: {
                    exportCSVName: "Export to Excel",
                }
            }}
            options={{
                addRowPosition: 'first',
                actionsColumnIndex: -1,
                exportButton: true,
                exportButton: {
                    csv: true,
                    pdf: false,
                },
                headerStyle:{backgroundColor: '#E30217', color: '#fff', }
            }}/>
        </div>
    )
}
