import React from 'react'
import './MaterialPopup.css'
import CloseIcon from '@mui/icons-material/Close'
import { Button, InputAdornment, makeStyles, TextField } from '@mui/material';
import { alpha, styled } from '@mui/material/styles';

const useStyles = makeStyles(theme =>({
    root: {
        backgroundColor: '#e30217',
        borderRadius: '15px'
    }
}));

const CssTextField = styled(TextField)({
    'width': '100%',
    '& label.Mui-focused': {
        color: 'black',
    },
    '& .MuiInput-underline:after': {
        borderBottomColor: '#e30217',
    },
    '& .MuiOutlinedInput-root': {
        '& fieldset': {
            borderColor: 'black',
        },
        '&:hover fieldset': {
            borderColor: '#e30217',
        },
        '&.Mui-focused fieldset': {
            borderColor: '#e30217',
        },
    },
});

function MaterialPopup(props) {
    const style = useStyles();
    return (props.trigger) ? (
        <div className='componentpopup'>
            <div className='popup-inner'>
                <div><button className='close-btn' onClick={() => props.setTrigger(false)}>
                    <CloseIcon style={{ 'color': "white", 'widtth': "100%", 'height': "100%", }} />
                </button></div>
                {props.children}
                <div className='popup-body'>
                    <form>
                        <div className='imagefield'>
                            <CssTextField label="Image Path" id="fullWidth" />
                        </div>
                        <div className='idname'>
                        <div className='idfield'>
                            <CssTextField label="Material ID" id="fullWidth"/>
                        </div>
                        <div className='namefield'>
                            <CssTextField label="Material Name" id="fullWidth" />
                        </div>
                        </div>
                        <div className='txtfield'>
                            <CssTextField label="Unit" id="fullWidth" />
                        </div>
                        <div className='txtfield'>
                            <CssTextField label="Amount" id="fullWidth" />
                        </div>
                        <div className='txtfield'>
                            <CssTextField label="Status" id="fullWidth" />
                        </div>
                        <div className='btngr'>
                            <Button variant="contained"
                            className={classes.root}>Add Material</Button>
                            <Button variant="contained" className='Button'>Cancel</Button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    ) : "";
}

export default MaterialPopup
